example.code:

using Syncfusion.UI.Xaml.Grid.Helpers;
var VisualContainer = this.dataGrid.GetVisualContainer();
//Set RowHeight to 2'nd row
VisualContainer.RowHeights[2] = 50;
VisualContainer.InvalidateMeasure();


using Syncfusion.UI.Xaml.TreeGrid.Helpers;
var treeGridPanel = this.treeGrid.GetTreePanel();
//Set RowHeight to 2'nd row
treeGridPanel.RowHeights[2] = 50;
treeGridPanel.InvalidateMeasure();

//The customized GridCellTextBoxRendererExt is replaced to CellRenderers collection after removed the default renderer of GridTextColumn.
this.dataGrid.CellRenderers.Remove("TextBox");
this.dataGrid.CellRenderers.Add("TextBox",new GridCellTextBoxRendererExt());


 this.dataGrid.TableSummaryRows.Add(new GridSummaryRow()
 {
    Name="Total Products",
    ShowSummaryInRow = true,
    Title = "Total Products Count: {ProductCount}",
    SummaryColumns = new ObservableCollection<ISummaryColumn>()
    {
        new GridSummaryColumn()
        {
            Name="ProductCount",
            MappingName="ProductName",
            SummaryType=SummaryType.CountAggregate,
            Format="{Count:d}"
        },
    }
});


	//Here, the Title and Name have the same ProductCount string.
